mapper.insert:
INSERT INTO ai_chat_messages (
    conversation_id, role, status, content, error_message
) VALUES (
         #{message.conversationId}, #{message.role}, #{message.status}, #{message.content}, #{message.errorMessage}
     )

mapper.select:
SELECT m.*
FROM ai_chat_messages m
         JOIN ai_chat_conversations c ON m.conversation_id = c.id
WHERE m.conversation_id = #{conversationId}
  AND c.email = #{email}
ORDER BY m.created_at ASC, m.id ASC

SELECT m.*
FROM ai_chat_messages m
         JOIN ai_chat_conversations c ON m.conversation_id = c.id
WHERE m.id = #{messageId}
  AND c.email = #{email}
LIMIT 1
