mapper.select:
SELECT email, username, password
FROM accounts
WHERE email = #{email}

SELECT COUNT(1) > 0
FROM accounts
WHERE email = #{email}

SELECT COUNT(1) > 0
FROM accounts
WHERE username = #{username}

SELECT email
FROM accounts
WHERE id = #{id}

SELECT id
FROM accounts
WHERE email = #{email}

mapper.update:
UPDATE accounts
SET username = #{username}
WHERE email = #{email}
