mapper.select:
SELECT COUNT(*)
FROM post_comments
WHERE post_id = #{postId}

mapper.insert:
INSERT INTO post_comments (
    post_id, author_email, content,created_at
)
VALUES (
   #{postId}, #{authorEmail}, #{content}, #{createdAt}
       )

mapper.update:
UPDATE post_comments
SET content = #{content}
WHERE id = #{id}

UPDATE post_comments
SET author_email = #{toEmail}
WHERE author_email = #{fromEmail}
