mapper.insert:
INSERT INTO posts (
    author_email, title, content, created_at, likes
)
VALUES (
   #{authorEmail}, #{title}, #{content}, #{createdAt}, #{likes}
       )

mapper.update:
UPDATE posts
SET title = #{title}, content = #{content}
WHERE id = #{id}

UPDATE posts
SET likes = likes + 1
WHERE id = #{postId}

UPDATE posts
SET likes = IF(likes > 0, likes - 1, 0)
WHERE id = #{postId}

UPDATE posts
SET author_email = #{toEmail}
WHERE author_email = #{fromEmail}
