mapper.update:
UPDATE exercise_records
SET exercise_id      = #{exerciseId},
    recorded_at      = #{recordedAt},
    duration_minutes = #{durationMinutes},
    calories         = #{calories}
WHERE id = #{id}
  AND email = #{email}

mapper.delete:
DELETE
FROM exercise_records
WHERE id = #{recordId}
  AND email = #{email}

mapper.select:
SELECT email
FROM exercise_records
WHERE id = #{recordId}
